import React from 'react';
import { useParams } from 'react-router-dom';
import products from '../data/products.json'; // ya tum jahan se products import karte ho

export default function ProductDetail() {
  const { id } = useParams();
  const product = products.find(p => p.id.toString() === id);

  if (!product) {
    return <h2 style={{ textAlign: 'center', marginTop: '50px' }}>Product not found</h2>;
  }

  return (
    <div style={{ maxWidth: '800px', margin: '40px auto', padding: '20px' }}>
      <img
        src={`/images/${product.image}`}
        alt={product.name}
        style={{ width: '100%', height: '400px', objectFit: 'cover', borderRadius: '8px' }}
      />
      <h1 style={{ marginTop: '20px' }}>{product.name}</h1>
      <p style={{ color: '#555', margin: '10px 0' }}>{product.description}</p>
      <h3>PKR {product.price.toLocaleString()}</h3>
      <button className="btn btn-primary">Add to Cart</button>
    </div>
  );
}
