import React from 'react';
import { CartContext } from '../App';
import { Link } from 'react-router-dom';

export default function ProductCard({ product }) {
  const { add } = React.useContext(CartContext);

  return (
    <div className="card">
      {/* Clickable Link around image & title */}
      <Link
        to={`/product/${product.id}`}
        style={{ textDecoration: 'none', color: 'inherit' }}
      >
        <img
          src={`/images/${product.image}`}
          alt={product.name}
          style={{ height: 200, width: '100%', objectFit: 'cover' }}
        />
        <div style={{ padding: 12 }}>
          <div style={{ fontWeight: 700, minHeight: 48 }}>{product.name}</div>
          <div style={{ fontSize: 12, color: '#555', minHeight: 40 }}>
            {product.description}
          </div>
        </div>
      </Link>

      {/* Price + Add to Cart stays outside link */}
      <div style={{ padding: '0 12px 12px 12px' }}>
        <div className="price" style={{ margin: '8px 0' }}>
          PKR {product.price.toLocaleString()}
        </div>
        <button
          className="btn btn-primary btn-block"
          onClick={() => add(product)}
        >
          Add to Cart
        </button>
      </div>
    </div>
  );
}
