import React, { useContext, useState } from 'react';
import { useParams } from 'react-router-dom';
import { CartContext } from '../App';

export default function ProductDetails({ products }) {
  const { id } = useParams();
  const { add } = useContext(CartContext);
  const product = products.find((p) => p.id.toString() === id);

  const [added, setAdded] = useState(false);

  if (!product) return <h2>Product not found</h2>;

  const handleAddToCart = () => {
    add(product);
    setAdded(true);
    setTimeout(() => setAdded(false), 1500); // reset after 1.5 sec
  };

  return (
    <div style={{ maxWidth: 900, margin: '0 auto', padding: 20 }}>
      {/* Large Image */}
      <img
        src={`/images/${product.image}`}
        alt={product.name}
        style={{
          width: '100%',
          maxHeight: 500,
          objectFit: 'cover',
          borderRadius: '8px',
          marginBottom: 20
        }}
      />

      {/* Product Info */}
      <h1 style={{ marginBottom: 10 }}>{product.name}</h1>
      <p style={{ fontSize: 16, color: '#555', marginBottom: 20 }}>
        {product.description}
      </p>
      <h2 style={{ marginBottom: 20 }}>PKR {product.price.toLocaleString()}</h2>

      {/* Add to Cart Button */}
      <button
        onClick={handleAddToCart}
        style={{
          padding: '12px 20px',
          fontSize: 16,
          border: 'none',
          borderRadius: 6,
          cursor: 'pointer',
          backgroundColor: added ? 'green' : '#ff4d6d',
          color: 'white',
          transition: '0.3s'
        }}
      >
        {added ? '✅ Added to Cart' : 'Add to Cart'}
      </button>
    </div>
  );
}
