import React from 'react';
import { useParams } from 'react-router-dom';
import products from '../data/products';
import { CartContext } from '../App';

export default function ProductDetail() {
  const { id } = useParams();
  const { add } = React.useContext(CartContext);

  // id number hai to Number(id) use karen
  const product = products.find((p) => p.id === Number(id));

  if (!product) {
    return (
      <div className="container">
        <h2>Product not found</h2>
      </div>
    );
  }

  return (
    <div className="container" style={{ marginTop: 24 }}>
      <div
        className="card"
        style={{
          padding: 16,
          display: 'grid',
          gridTemplateColumns: '1fr 1fr',
          gap: 24
        }}
      >
        <img
          src={`/images/${product.image}`}
          alt={product.name}
          style={{ width: '100%', height: 380, objectFit: 'cover', borderRadius: 12 }}
        />
        <div>
          <h2 style={{ marginTop: 0 }}>{product.name}</h2>
          <div className="price" style={{ margin: '12px 0' }}>
            PKR {product.price.toLocaleString()}
          </div>
          <p style={{ color: '#555' }}>{product.description}</p>
          <button className="btn btn-primary" onClick={() => add(product)}>
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
}
