function ProductsPage() {
  const [q, setQ] = useState('');

   // ✅ Page open hote hi scroll top
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  const filtered = useMemo(
    () => products.filter((p) => p.name.toLowerCase().includes(q.toLowerCase())),
    [q]
  );
  return (
    <div className="container">
      <h2>All Products</h2>
      <input
        className="input"
        placeholder="Search products..."
        value={q}
        onChange={(e) => setQ(e.target.value)}
        style={{ margin: '12px 0' }}
      />
      <div className="grid" style={{ gap: '20px' }}> 
        {filtered.map((p) => (
          <ProductCard key={p.id} product={p} />
        ))}
      </div>
    </div>
  );
}


export default function App() {
  const [cart, setCart] = useState([]);

  const add = (item) =>
    setCart((prev) => {
      const found = prev.find((x) => x.id === item.id);
      if (found) {
        return prev.map((x) =>
          x.id === item.id ? { ...x, qty: x.qty + 1 } : x
        );
      }
      return [...prev, { ...item, qty: 1 }];
    });

  const remove = (id) => setCart((prev) => prev.filter((x) => x.id !== id));
  const inc = (id) =>
    setCart((prev) =>
      prev.map((x) => (x.id === id ? { ...x, qty: x.qty + 1 } : x))
    );
  const dec = (id) =>
    setCart((prev) =>
      prev.map((x) =>
        x.id === id ? { ...x, qty: Math.max(1, x.qty - 1) } : x
      )
    );
  const clear = () => setCart([]);
  const total = useMemo(
    () => cart.reduce((s, x) => s + x.price * x.qty, 0),
    [cart]
  );

  const ctx = { cart, add, remove, inc, dec, clear, total };

  return (
    <CartContext.Provider value={ctx}>
      <Navbar />
      <Routes>
        <Route path="/" element={<Home products={products} />} /> {/* ✅ Correct Home */}
        <Route path="/products" element={<ProductsPage />} />
        <Route path="/about" element={<About />} />
        <Route path="/cart" element={<Cart />} />
        <Route path="/checkout" element={<Checkout />} />
        <Route path="/product/:id" element={<ProductDetail />} />
      </Routes>
      <footer className="footer">
        © {new Date().getFullYear()} Ideal Expressions — Built with ❤️
      </footer>
    </CartContext.Provider>
  );
}
