import React, { createContext, useContext, useMemo, useState } from 'react';
import { Routes, Route, Link, useNavigate } from 'react-router-dom';
import products from './data/products';
import Navbar from './components/Navbar';
import ProductCard from './components/ProductCard';
import Cart from './components/Cart';
import Checkout from './components/Checkout';

// About Page Component
function About() {
  return (
    <div className="container">
      <h1>About Us</h1>
      <p>
        Welcome to <strong>Ideal Expressions</strong> — your trusted destination
        for authentic beauty products. We pride ourselves on delivering only
        original items, ensuring the highest quality for our customers.
        Thousands of satisfied buyers trust us for our commitment to excellence
        and quick delivery. Shop with confidence and experience the Ideal
        Expressions difference!
      </p>
    </div>
  );
}

export const CartContext = createContext();

function Home() {
  return (
    <div>
      <div className="header">
        <div className="container">
          <h1>Ideal Expressions</h1>
          <p>
            Modern beauty store — Quick checkout via PayPal or Cash on Delivery.
          </p>
          <div style={{ display: 'flex', gap: 12, marginTop: 16 }}>
            <Link to="/products" className="btn btn-primary">
              Shop Products
            </Link>
            <a
              href="https://vercel.com"
              target="_blank"
              rel="noreferrer"
              className="btn btn-outline"
            >
              Free Hosting
            </a>
          </div>
        </div>
      </div>

      <div className="container" style={{ paddingBottom: "40px" }}>
        <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>Trending Now</h2>

        {/* Products Center Align */}
        <div
          className="grid"
          style={{
            marginTop: 12,
            justifyContent: 'center',
            gap: '24px',
            display: 'flex',
            flexWrap: 'wrap',
          }}
        >
          {products.slice(0, 3).map((p) => (
            <div style={{ transform: 'scale(1.1)' }} key={p.id}>
              <ProductCard product={p} />
            </div>
          ))}
        </div>

        {/* View More Button - Always Below */}
        <div style={{ textAlign: 'center', marginTop: '30px' }}>
          <Link to="/products" className="btn btn-outline">
            View More Products
          </Link>
        </div>
      </div>
    </div>
  );
}

function ProductsPage() {
  const [q, setQ] = useState('');
  const filtered = useMemo(
    () => products.filter((p) => p.name.toLowerCase().includes(q.toLowerCase())),
    [q]
  );
  return (
    <div className="container">
      <h2>All Products</h2>
      <input
        className="input"
        placeholder="Search products..."
        value={q}
        onChange={(e) => setQ(e.target.value)}
        style={{ margin: '12px 0' }}
      />
      <div className="grid">
        {filtered.map((p) => (
          <ProductCard key={p.id} product={p} />
        ))}
      </div>
    </div>
  );
}

export default function App() {
  const [cart, setCart] = useState([]);
  const add = (item) =>
    setCart((prev) => {
      const found = prev.find((x) => x.id === item.id);
      if (found) {
        return prev.map((x) =>
          x.id === item.id ? { ...x, qty: x.qty + 1 } : x
        );
      }
      return [...prev, { ...item, qty: 1 }];
    });
  const remove = (id) => setCart((prev) => prev.filter((x) => x.id !== id));
  const inc = (id) =>
    setCart((prev) =>
      prev.map((x) => (x.id === id ? { ...x, qty: x.qty + 1 } : x))
    );
  const dec = (id) =>
    setCart((prev) =>
      prev.map((x) =>
        x.id === id ? { ...x, qty: Math.max(1, x.qty - 1) } : x
      )
    );
  const clear = () => setCart([]);
  const total = useMemo(
    () => cart.reduce((s, x) => s + x.price * x.qty, 0),
    [cart]
  );

  const ctx = { cart, add, remove, inc, dec, clear, total };

  return (
    <CartContext.Provider value={ctx}>
      <Navbar />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/products" element={<ProductsPage />} />
        <Route path="/about" element={<About />} /> {/* About Page Route */}
        <Route path="/cart" element={<Cart />} />
        <Route path="/checkout" element={<Checkout />} />
      </Routes>
      <footer className="footer">
        © {new Date().getFullYear()} Ideal Expressions — Built with ❤️
      </footer>
    </CartContext.Provider>
  );
}
