import React from 'react';
import { useParams } from 'react-router-dom';
import { CartContext } from '../App';

export default function ProductDetails({ products }) {
  const { id } = useParams();
  const { add } = React.useContext(CartContext);
  const [added, setAdded] = React.useState(false);

  const product = products.find((p) => p.id.toString() === id);

  if (!product) {
    return <h2 style={{ textAlign: 'center', marginTop: 50 }}>Product not found</h2>;
  }

  const handleAddToCart = () => {
    add(product);
    setAdded(true);
    setTimeout(() => setAdded(false), 1500);
  };

  return (
    <div style={{ maxWidth: 900, margin: '40px auto', padding: '0 20px' }}>
      {/* Full width image */}
      <img
        src={`/images/${product.image}`}
        alt={product.name}
        style={{
          width: '100%',
          maxHeight: 500,
          objectFit: 'cover',
          borderRadius: 8,
          marginBottom: 20
        }}
      />

      {/* Product Info */}
      <h2 style={{ fontWeight: 700 }}>{product.name}</h2>
      <p style={{ fontSize: 14, color: '#555', marginTop: 8 }}>
        {product.description}
      </p>
      <div style={{ fontSize: 18, fontWeight: 600, margin: '12px 0' }}>
        PKR {product.price.toLocaleString()}
      </div>

      {/* Add to Cart with feedback */}
      <button
        onClick={handleAddToCart}
        style={{
          backgroundColor: added ? 'green' : '#ff4d6d',
          border: 'none',
          padding: '10px 20px',
          color: '#fff',
          fontSize: 16,
          fontWeight: 600,
          borderRadius: 5,
          cursor: 'pointer',
          transition: '0.3s'
        }}
      >
        {added ? '✅ Added!' : 'Add to Cart'}
      </button>
    </div>
  );
}
